// TILT CAMERA toward helix
camera.rotation.x = -0.15;
camera.position.set(0, 0.2, 12);

// PARALLAX on scroll
window.addEventListener('scroll', () => {
  const t = window.scrollY / (document.body.scrollHeight - window.innerHeight);
  group.rotation.x = t * 0.2 - 0.1;
});
